// Обработка ошибки создания видео
const videoStatus = $input.item.json.status;
const videoId = $input.item.json.videoId || 'unknown';

// Логируем ошибку
console.log(`❌ ВИДЕО УПАЛО! ID: ${videoId}, Статус: ${videoStatus}`);

// Возвращаем информацию об ошибке
return {
  json: {
    error: true,
    message: `Видео ${videoId} не удалось создать. Статус: ${videoStatus}`,
    videoId: videoId,
    status: videoStatus,
    timestamp: new Date().toISOString(),
    action: 'STOP_WORKFLOW'
  }
};
